    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String City= jTextField1.getText();
        if(City.equals("")){
            JOptionPane.showMessageDialog(null, "No puede ser vacio");
        }else{
            boolean insert= Cit.insertCity(City);
            if(insert==false){
                JOptionPane.showMessageDialog(null, "Ciudad repetida");
                jTextField1.setText(null);
            }else{
                JOptionPane.showMessageDialog(null, "Insertada correctamente");
                jTextField1.setText(null);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

