    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String cit= jTextField2.getText();
        City val= Cit.searchCity(cit);
        if(val==null){
            JOptionPane.showMessageDialog(null, "La ciudad ingresada no existe");
        }else{
            boolean result= Cit.deleteCity(val);
            if(result==true){
                JOptionPane.showMessageDialog(null, "Eliminada correctamente la ciudad");
            }else{
                JOptionPane.showMessageDialog(null, "No se pudo eliminar");
            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        String name= jTextField3.getText();
        City val= Cit.searchCity(name);
        if(val==null){
            JOptionPane.showMessageDialog(null, "No existe la ciudad");
            
        }else{
            boolean edit= Cit.editCity(val);
            if(edit==true){
                 JOptionPane.showMessageDialog(null, "Editada correctamente");
            }else{
                 JOptionPane.showMessageDialog(null, "Ocurrio un error");                               
            }
        }                
    }//GEN-LAST:event_jButton4ActionPerformed

